INSERT INTO tb_book (title, genre, release_year, description) VALUES ('To Kill a Mockingbird', 'Classic Literature', 1960, 'A powerful novel set in the racially charged Deep South, following young Scout Finch as she witnesses her father, Atticus, defend a black man unjustly accused of a crime. The story explores themes of racial injustice, morality, and childhood innocence.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('1984', 'Classic Literature', 1949, 'A dystopian masterpiece that envisions a totalitarian world where the government, led by Big Brother, controls every aspect of life. The novel follows Winston Smith as he struggles for truth and freedom in a society of surveillance and propaganda.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Pride and Prejudice', 'Classic Literature', 1813, 'A timeless romance that follows Elizabeth Bennet as she navigates issues of class, reputation, and love in Regency-era England. Her interactions with the proud and enigmatic Mr. Darcy create one of the most beloved literary relationships of all time.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('The Great Gatsby', 'Classic Literature', 1925, 'A poignant critique of the American Dream, set in the Roaring Twenties. The novel tells the tragic story of Jay Gatsby, a mysterious millionaire, and his obsession with the beautiful but unattainable Daisy Buchanan.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Moby Dick', 'Classic Literature', 1851, 'An epic tale of obsession and revenge, following Captain Ahab''s relentless pursuit of the elusive white whale, Moby Dick. The novel explores themes of fate, free will, and the nature of humanity.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Sapiens: A Brief History of Humankind', 'Historical', 2011, 'A groundbreaking exploration of the history of Homo sapiens, from our early ancestors to the modern age. Yuval Noah Harari examines how human societies have evolved through biology, culture, and economics.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Educated: A Memoir', 'Historical', 2018, 'Tara Westover recounts her journey from growing up in a strict and abusive survivalist household in rural Idaho to earning a PhD from Cambridge University. A moving story of resilience and the power of education.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('The Wright Brothers', 'Historical', 2015, 'A detailed biography of Orville and Wilbur Wright, chronicling their journey from bicycle mechanics to pioneers of human flight, and the struggles they faced in revolutionizing aviation.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Becoming', 'Historical', 2018, 'Former First Lady Michelle Obama shares her personal journey, from her childhood in Chicago to her time in the White House, offering an inspiring and intimate portrait of her life.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('The Immortal Life of Henrietta Lacks', 'Historical', 2010, 'The fascinating true story of Henrietta Lacks, whose cancer cells were unknowingly taken and used for groundbreaking scientific research, raising important ethical questions about medical consent.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Dune', 'Science Fiction', 1965, 'A sprawling space opera set on the desert planet Arrakis, where noble families battle for control over the most valuable resource in the universe. Paul Atreides, heir to House Atreides, must embrace his destiny in this epic tale of politics, prophecy, and survival.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Neuromancer', 'Science Fiction', 1984, 'A cyberpunk classic that follows Case, a washed-up hacker, as he is recruited for a dangerous mission in a world dominated by artificial intelligence, virtual reality, and corporate espionage.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Foundation', 'Science Fiction', 1951, 'A visionary tale of the decline and rebirth of a galactic empire, following mathematician Hari Seldon and his creation of the science of psychohistory to predict and shape the future.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Ender''s Game', 'Science Fiction', 1985, 'A thrilling story about Ender Wiggin, a child prodigy trained in a futuristic battle school to lead humanity in a war against an alien species. A deep exploration of strategy, morality, and leadership.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('The War of the Worlds', 'Science Fiction', 1898, 'A groundbreaking alien invasion novel depicting humanityâ€™s struggle against an advanced Martian force, with themes of survival, colonialism, and scientific progress.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('The Hobbit', 'Fantasy', 1937, 'A delightful adventure following Bilbo Baggins, an unassuming hobbit who is swept into a quest to reclaim a lost dwarf kingdom from the dragon Smaug. A tale of courage, friendship, and self-discovery.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('Harry Potter and the Sorcerer''s Stone', 'Fantasy', 1997, 'The magical beginning of the beloved series, following Harry Potter as he discovers his identity as a wizard and embarks on a journey to Hogwarts School of Witchcraft and Wizardry.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('The Name of the Wind', 'Fantasy', 2007, 'The captivating story of Kvothe, a gifted young man with a mysterious past, as he recounts his rise from a child performer to a legendary figure with unmatched magical abilities.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('A Game of Thrones', 'Fantasy', 1996, 'The first book in the A Song of Ice and Fire series, set in a brutal medieval world where noble families vie for power in a kingdom filled with intrigue, war, and supernatural forces.');
INSERT INTO tb_book (title, genre, release_year, description) VALUES ('The Fellowship of the Ring', 'Fantasy', 1954, 'The first installment of The Lord of the Rings, chronicling the journey of Frodo Baggins as he sets out to destroy the One Ring and defeat the Dark Lord Sauron.');
